CREATE TABLE `comments`( `ID` int(11) NOT NULL, `UserCreated` int(11) NOT NULL, `DateCreated` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `Content` text NOT NULL, `Likes` int(11) NOT NULL, `Dislikes` int(11) NOT NULL, `ReplyTo` int(11) DEFAULT NULL, `TopicID` int(11) NOT NULL) ENGINE=MyISAM DEFAULT CHARSET=utf8; CREATE TABLE `statuses` ( `ID` int(11) NOT NULL, `Name` varchar(50) NOT NULL, `Color` varchar(100) NOT NULL ) ENGINE=InnoDB DEFAULT CHARSET=utf8; CREATE TABLE `topics` ( `ID` int(11) NOT NULL, `Name` varchar(150) NOT NULL, `Content` text, `UserCreated` int(11) NOT NULL, `DateCreated` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP, `Status` int(11) NOT NULL, `Likes` smallint(6) NOT NULL, `Dislikes` smallint(6) NOT NULL ) ENGINE=InnoDB DEFAULT CHARSET=utf8; CREATE TABLE `users` ( `ID` int(11) NOT NULL, `Email` tinytext NOT NULL, `Username` tinytext NOT NULL, `Password` tinytext NOT NULL, `Avatar` varchar(500) DEFAULT 'http://chocolatevent.by/sites/default/files/noavatar.png', `Type` int(11) NOT NULL DEFAULT '0' ) ENGINE=InnoDB DEFAULT CHARSET=utf8; ALTER TABLE `comments` ADD PRIMARY KEY (`ID`); ALTER TABLE `statuses` ADD PRIMARY KEY (`ID`); ALTER TABLE `topics` ADD PRIMARY KEY (`ID`); ALTER TABLE `users` ADD PRIMARY KEY (`ID`); ALTER TABLE `comments` MODIFY `ID` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=2; ALTER TABLE `statuses` MODIFY `ID` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=7; ALTER TABLE `topics` MODIFY `ID` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=3; ALTER TABLE `users` MODIFY `ID` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=2;
